    // const handleEdit = (e, index) => {
    //     document.querySelector('#text').value = tasks[index].name;
    //     handleDelete(e, index)
    //     // setTasks([...tasks]);
    //   }
    const fnPvm = (e) => {
        console.log('fnPvm')
        let a = e.target.value;
        let b = sumBePvm;//Activ???
        let c = b * a / 100;
        let d = b * (a / 100 + 1);//Activ???
        setPvm(a);
        setSumBePvm(b);
        setOnlyPvm(c);
        setTotal(d);
    }

    const fnSumBePvm = (val) => {
        console.log('fnSumBePvm')
        let b = val;
        let a = pvm;

        let c = b * a / 100;
        let d = b * (a / 100 + 1);
        //    setPvm(a);
        setSumBePvm(b);
        setOnlyPvm(c);
        setTotal(d);
    }
    const fnTotal = (val) => {
        console.log('fnTotal')
        let d = val;
        let a = pvm;
        let b = d / (a / 100 + 1);
        let c = b * a / 100;
        //    setPvm(a);
        setSumBePvm(b);
        setOnlyPvm(c);
        setTotal(d);
    }




    return (
        <main>
            <form>

                <div className='col' style={{ alignItems: 'flex-end' }}>
                    <label>PVM tarifas</label>
                    <label>Suma (be PVM)</label>
                    <label>PVM suma</label>
                    <label>Bendra suma (su PVM) </label>
                </div>
                <div className='col'>
                    <select onChange={fnPvm} name="vat_percent" id="vat_percent">
                        <option value="21">21%</option>
                        <option value="9">9%</option>
                        <option value="5">5%</option>
                    </select>
                    <input onChange={ (e) => fnSumBePvm(e.target.value)} type="text" id="sumBePvm" defaultValue={0} />
                    <input type="text" id="sumPvm" defaultValue={''} disabled="disabled" />
                    <input onChange={ (e) => fnTotal(e.target.value)} type="text" id="total" defaultValue={0} />
                </div>

            </form>

            <div>Įveskite sumą be PVM arba bendrą sumą (su PVM).</div>
        </main>
    );

}


