    const [tasks, setTasks] = useState([]);



    const fnAdd = (event) => {
        event.preventDefault();
        const obj = event.target;
        console.log(obj.fItem);
        console.log(obj.fQty)
        console.log(obj.fItem.value);
        console.log(obj.fQty.value)
        // console.log(localStorage.getItem('items'));

        const xxx = {
            fItem: 'aaa',
            fQty: 'bbb'
        };

        setTasks([...tasks, xxx]);
        console.log(tasks)

        // const xKint = { itemName: 'prekPav1', qty: 2 };
        // let xArr = [...tasks, xKint];
        // setTasks(xArr);
        // localStorage.setItem('items', JSON.stringify(tasks))//irasom i atminti JSON formatu 

        // console.log(localStorage.getItem('items')) //gaunam is atminties



    }

    // useEffect(() => {
    //     const data = localStorage.getItem('tasks');
    //     setTasks(JSON.parse(data));
    //     // console.log('Atvaizduoti Sand sarasa')
    //     // console.log(localStorage.getItem('items'));
    // }, [tasks]);



    return (
        <form onSubmit={fnAdd}>
            <label>Prekė
                <input type="text" name="fItem" />
            </label>
            <label>Kiekis
                <input type="number" name="fQty" />
            </label>
            <button >Pridėti</button>
        </form>
    );
}
export default Adds;
