import React from 'react';
import { useId, useState, useEffect } from 'react';

const Adds = () => {

    const [tasks, setTasks] = useState(JSON.parse(localStorage.getItem('items')));

    const fnAdd = (event) => {
        event.preventDefault();
        const obj = event.target;
        // console.log(obj.fItem);
        // console.log(obj.fQty)
        // console.log(obj.fItem.value + ' / ' + obj.fQty.value);
        let data;
        data = [...tasks, { fItem: obj.fItem.value, fQty: obj.fQty.value }];
        setTasks(data);
        localStorage.setItem('items', JSON.stringify(data));
        // console.log(data);
        // console.log(tasks);

    }

    useEffect(() => {
        // const data = localStorage.getItem('tasks');
        // setTasks(JSON.parse(data));
        // console.log('Atvaizduoti Sand sarasa')

        const store = JSON.parse(localStorage.getItem('items'));
        console.log(store);
        let addTag;
        if (store.length > 0) {
            store.map((product, index) => {
                addTag += <tr>
                    <td>{product.item}</td>
                    <td>{product.qty}</td>
                    <td><button onclick="fnDeleteItem({index})" class="btn btn-danger">Šalintti</button></td></tr>
            })
        }
        else {
            addTag = <td colspan="3"><p style="background-color: yellow; color: blue;">Krepšelyje kol kas neturite jokių prekių.</p></td>;
        }
        // document.querySelector('tbody').innerHTML = addTag;
        console.log(addTag);



    }, [tasks]);





    return (
        <form onSubmit={fnAdd}>
            <label>Prekė
                <input type="text" name="fItem" />
            </label>
            <label>Kiekis
                <input type="number" name="fQty" />
            </label>
            <button >Pridėti</button>
        </form>
    );
}
export default Adds;
