import React, { useEffect, useState } from "react";

const Sprend = () => {
    const APIurl = 'https://api.adviceslip.com/advice';
    const [advices, setAdvices] = useState([]);

    useEffect(() => {
        const store = JSON.parse(localStorage.getItem('advices'));
        setAdvices(store);
        Advice()
    }, [])

    const Advice = () => {
        // console.log('Bum..............')
        const Xxx = () => {
            fetch(APIurl).then(resp => resp.json()).then(resp => {
                console.log(resp.slip.id + ': ' + resp.slip.advice)
                const data = [...advices, { id: resp.slip.id, count: 1, advice: resp.slip.advice }];
                setAdvices(data);
                localStorage.setItem('advices', JSON.stringify(data));
                return <p>resp.slip.advice</p>;
            });
        }
    }

    return (
        <>
            <button type="button" onClick={Advice}>Patark!!!</button>
            <div>
                <Advice Xxx/>

                {/* {
                advices.map(a =>
                    <p key={a.id}>{a.advice}</p>
                )
            } */}

            </div>
        </>
    );
}
export default Sprend
