const Sprend = () => {
    const APIurl = 'https://api.adviceslip.com/advice';
    const [advices, setAdvices] = useState([]);

    const Advice = () => {
        fetch(APIurl).then(resp => resp.json()).then(resp => {
            console.log(resp.slip.id)
            console.log(resp.slip.advice)
            const data = [...advices, { id: resp.slip.id, advice: resp.slip.advice }];
            setAdvices(data);
            localStorage.setItem('advices', JSON.parse(advices))

        });
    }
    Advice();


    return (<>


        <button onSubmit={Advice}>Patark!!!</button>
